import React, { useState, useEffect, useRef } from 'react';
import './Main.scss'
// Пример данных продуктов
const initialProducts = [
  { id: 1, name: 'Product 1', imageUrl: 'data:image/jpeg;base64,' },
  { id: 2, name: 'Product 2', imageUrl: 'data:image/jpeg;base64,' },
  { id: 3, name: 'Product 3', imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/65/Product_Photography.jpg/2560px-Product_Photography.jpg' },
  { id: 4, name: 'Product 1', imageUrl: 'data:image/jpeg;base64,' },
  { id: 5, name: 'Product 2', imageUrl: 'data:image/jpeg;base64,' },
  { id: 6, name: 'Product 2', imageUrl: 'data:image/jpeg;base64,' },
  { id: 7, name: 'Product 3', imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/65/Product_Photography.jpg/2560px-Product_Photography.jpg' },
  { id: 8, name: 'Product 1', imageUrl: 'data:image/jpeg;base64,' },
  { id: 9, name: 'Product 2', imageUrl: 'data:image/jpeg;base64,' },
  { id: 10, name: 'Product 3', imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/65/Product_Photography.jpg/2560px-Product_Photography.jpg' },
  { id: 11, name: 'Product 1', imageUrl: 'data:image/jpeg;base64,' },
  { id: 12, name: 'Product 2', imageUrl: 'data:image/jpeg;base64,' },
  { id: 13, name: 'Product 3', imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/65/Product_Photography.jpg/2560px-Product_Photography.jpg' },
  { id: 14, name: 'Product 1', imageUrl: 'data:image/jpeg;base64,' },
  { id: 21, name: 'Product 2', imageUrl: 'data:image/jpeg;base64,' },
//   30, name: 'Product 3', imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/65/Product_Photography.jpg/2560px-Product_Photography.jpg' },
];

const ProductsList: React.FC = () => {
  const [products, setProducts] = useState(initialProducts); // Состояние для продуктов
  const [loading, setLoading] = useState(false); // Состояние для загрузки
  const [hasMore, setHasMore] = useState(true); // Состояние для проверки наличия новых данных
  const loadMoreRef = useRef(null); // Ссылка на кнопку загрузки

  // Функция для подгрузки дополнительных продуктов
  const loadMoreProducts = () => {
    if (loading || !hasMore) return;

    setLoading(true);

    // Эмуляция задержки для подгрузки данных (например, с API)
    setTimeout(() => {
      const newProducts = [
        { id: 15, name: 'Product 2', imageUrl: 'data:image/jpeg;base64,' },
        { id: 16, name: 'Product 2', imageUrl: 'data:image/jpeg;base64,' },
        { id: 17, name: 'Product 3', imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/65/Product_Photography.jpg/2560px-Product_Photography.jpg' },
        { id: 18, name: 'Product 1', imageUrl: 'data:image/jpeg;base64,' },
        { id: 19, name: 'Product 2', imageUrl: 'data:image/jpeg;base64,' },
        { id: 20, name: 'Product 3', imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/65/Product_Photography.jpg/2560px-Product_Photography.jpg' },
        { id: 22, name: 'Product 3', imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/65/Product_Photography.jpg/2560px-Product_Photography.jpg' },
        { id: 23, name: 'Product 1', imageUrl: 'data:image/jpeg;base64,' },
        { id: 24, name: 'Product 2', imageUrl: 'data:image/jpeg;base64,' },
        { id: 25, name: 'Product 3', imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/65/Product_Photography.jpg/2560px-Product_Photography.jpg' },
        { id: 26, name: 'Product 2', imageUrl: 'data:image/jpeg;base64,' },
        { id: 27, name: 'Product 3', imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/65/Product_Photography.jpg/2560px-Product_Photography.jpg' },
        { id: 28, name: 'Product 1', imageUrl: 'data:image/jpeg;base64,' },
        { id: 29, name: 'Product 2', imageUrl: 'data:image/jpeg;base64,' },
        { id: 29, name: 'Product 2', imageUrl: 'data:image/jpeg;base64,' },
      ];
      setProducts((prev) => [...prev, ...newProducts]);
      setLoading(false);
      if (products.length >= 20) setHasMore(false); // Если достигли лимита, убираем кнопку
    }, 1000);
  };

  // Логика для бесконечной прокрутки
  const observer = useRef(
    new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          loadMoreProducts();
        }
      },
      { threshold: 1.0 }
    )
  );

  useEffect(() => {
    const currentObserver = observer.current;
    if (loadMoreRef.current) {
      currentObserver.observe(loadMoreRef.current);
    }

    return () => {
      if (loadMoreRef.current) {
        currentObserver.unobserve(loadMoreRef.current);
      }
    };
  }, []);

  return (
    <div className="products-container">
      <div className="products-grid">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.imageUrl} alt={product.name} />
            <h3>{product.name}</h3>
          </div>
        ))}
      </div>

      {loading && <p>Loading...</p>}

      {/* Кнопка "Загрузить еще" */}
      {hasMore && !loading && (
        <button ref={loadMoreRef} onClick={loadMoreProducts} className="load-more-btn">
          Load more
        </button>
      )}
    </div>
  );
};

export default ProductsList;
